import { useRef } from "react";
import Input from "../../UI/Input";
import classes from "./MealItemForm.module.css";

const MealItemForm = () => {
  const amountInputRef = useRef();

  const submitHandler = (e) => {
    e.preventDefault();

    if (
      amountInputRef.trim().length === 0 ||
      amountInputRef > 1 ||
      amountInputRef < 5
    ) {
        return 
    }
  };
  return (
    <form className={classes.form} onSubmit={submitHandler}>
      <Input
        ref={amountInputRef}
        label="amount"
        input={{
          id: "amount",
          type: "number",
          min: "1",
          max: "5",
          step: "1",
          defaultValue: "1",
        }}
      />
      <button>+ Add</button>
    </form>
  );
};

export default MealItemForm;
